-- ----------------------------
-- Function structure for wp_fn_realmedialibrary_childs
-- ----------------------------
DROP FUNCTION IF EXISTS `wp_fn_realmedialibrary_childs`;
delimiter ;;
CREATE FUNCTION `wp_fn_realmedialibrary_childs`(rootId INT, _useTempChildTableForCheck BOOLEAN)
 RETURNS varchar(1000) CHARSET utf8mb4
  READS SQL DATA 
  SQL SECURITY INVOKER
BEGIN
        	DECLARE sTemp VARCHAR(1000);
        	DECLARE sTempChd VARCHAR(1000);
        	SET sTemp = '$';
        	SET sTempChd = CAST(rootId AS CHAR);
		    SET SESSION group_concat_max_len = 100000;
        	WHILE sTempChd IS NOT NULL DO
        		SET sTemp = CONCAT(sTemp,',',sTempChd);
        		IF _useTempChildTableForCheck IS NULL OR _useTempChildTableForCheck = false THEN
        		    SELECT GROUP_CONCAT(id) INTO sTempChd FROM wp_realmedialibrary WHERE FIND_IN_SET(parent,sTempChd) > 0;
        		ELSE
        		    SELECT GROUP_CONCAT(id) INTO sTempChd FROM wp_realmedialibrary_tmp WHERE FIND_IN_SET(parent,sTempChd) > 0;
        		END IF;
        	END WHILE;
        	RETURN sTemp;
        END
;;
delimiter ;

SET FOREIGN_KEY_CHECKS = 1;
